#include <stdio.h>

int main(){
int x = 23;
int y = 31;
int soma, subtracao, divisao, multiplicacao;

soma = x + y;
subtracao = x - y;
divisao = x / y;
multiplicacao = x * y;

printf("A soma é: %d\n" , soma);
printf("A subtração é: %d\n" , subtracao);
printf("A divisão é: %d\n" , divisao);
printf("A multiplicação é: %d\n" , multiplicacao);

return 0;






}