#include <stdio.h>

int main(){
float delta = 6.7;
float beta = 9.5;
float produto = delta * beta;
float quociente = delta / beta;
float soma = delta + beta;
float subtracao = delta - beta;

printf("O resultado do produto é: %2.f\n" , produto);
printf("O resultado do quociente é: %2.f\n" , quociente);
printf("O resultado da soma é: %2.f\n" , soma);
printf("O resultado da subtracao é: %2.f\n" , subtracao);

return 0;








}